let overlay=document.getElementById("overlay");let modal=document.getElementById("modal");const Submit=()=>{let name=document.getElementById("name").value;let email=document.getElementById("email").value;let message=document.getElementById("message").value;let error=document.getElementById("error_contact");let success=document.getElementById("success_contact");if(name===""||email===""||message===""){console.log("Error: Llenar todos los campos");error.style.display="block";success.style.display="none"}else{fetch("http://localhost:3000/contacto",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({nombreContacto:name,email:email,message:message})}).then(res=>{console.log(res);return res.json()}).then(async data=>{if(data){error.style.display="none";success.style.display="block";overlay.style.visibility="visible";overlay.style.opacity="100%";modal.style.visibility="visible";modal.style.opacity="100%"}})}};const openModal=()=>{overlay.style.visibility="visible";overlay.style.opacity="100%";modal.style.visibility="visible";modal.style.opacity="100%"};const closeModal=()=>{overlay.style.visibility="hidden";overlay.style.opacity="0";modal.style.visibility="hidden";modal.style.opacity="0"};